  },[]);*/
  return(
    <div className="App">
      <header>
        <h1>Hello World</h1>
      </header>
      <section>
        <Grid style={{padding:'20px'}}>
          {books.map( books =>
            <Grid item xs={12} lg={4}>
              <BookCard title={books.title}/>
            </Grid>
          )}
        </Grid>
      </section>
    </div>
  );
}

export default App;
