import React, {useState} from 'react';
import { makeStyles, createMuiTheme, responsiveFontSizes } from '@material-ui/core/styles';
import Typography from '@material-ui/core/Typography';
import Card from '@material-ui/core/Card';
import CardActionArea from '@material-ui/core/CardActionArea';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import CardMedia from '@material-ui/core/CardMedia';
//import StarBorderIcon from '@material-ui/icons/StarBorder';
import StarIcon from '@material-ui/icons/Star';
import { yellow, grey } from '@material-ui/core/colors';
import { Grid } from '@material-ui/core';

const useStyles = makeStyles({
    root: {
      maxWidth: 400,
      maxHeight: 600,
    },
    media: {
      maxWidth: 300,
      minWidth: 200,
      maxHeight: 350,
      minHeight: 250,
    },
  });

let theme = createMuiTheme();
theme = responsiveFontSizes(theme);

export default function BookCard(props) {
  const [starIcon, setStarIcon] = useState(false);
  const [starIconColor, setStarIconColor] = useState(grey);
  const classes = useStyles();

  const favIcon = () => {
    if(starIcon === false) {
      setStarIcon(true);
      setStarIconColor(yellow[400]);
    } else {
      setStarIcon(false);
      setStarIconColor(grey);
    }
  }

  return (
    <div className="BookCard">
        <Card className={classes.root} elevation={3}>
          <CardActionArea>
                <CardMedia
                  className={classes.media}
                  component="img"
                  alt="books"           
                  image={props.books.img}

                  />
              <CardContent theme={theme}>
                  <Typography gutterBottom variant="h5" component="h2">
                      {props.books.title}
                  </Typography>
                  <Typography variant="h6" color="textSecondary" component="p">
                      {props.books.author}
                  </Typography>
              </CardContent>
          </CardActionArea>
          <CardActions>
              <Grid>
                <button onClick={favIcon()}><StarIcon color={starIconColor}/></button>
              </Grid>
          </CardActions>
        </Card>
    </div>
  );
}
