import React, {useState, useEffect}  from 'react';
import BookCard from './BookCard';
import Grid from '@material-ui/core/Grid';

export default function ShowBook(props) {
    const [books, setBooks] = useState([]);

    //fetch with the backend
    useEffect(() => {
        const fetchData = async() => {

        const result = await fetch('http://localhost:8888/api/books')
        .then((response) => {
        return response.json();
        })
        .then((response) => {
            console.log(response)
            setBooks(response);
        })
        .catch(err => console.log(err))
        };
        fetchData();
    },[]);

    return(
        <div className="ShowBook">
            <Grid style={{padding:'24px', color: '#455a64'}} container spacing={10}>
            {books.map((book, index) =>
                <Grid key={index} item xs={12} sm={4} md={3} lg={3}>
                <BookCard books={book} />
            </Grid>
            )}
            </Grid>
        </div>
    );
}
