import React, {useState, useEffect} from 'react';
import Grid from '@material-ui/core/Grid';
import BookCard from './components/bookCard';
import { makeStyles } from '@material-ui/core/styles';
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import Typography from '@material-ui/core/Typography';
import teal from '@material-ui/core/colors/teal';
import grey from '@material-ui/core/colors/grey';

function App() {
  const [books, setBooks] = useState([]);

  //fetch with the backend
  useEffect(() => {
    fetch('http://localhost:8888/api/books')
    .then((response) => {
      return response.json();
    })
    .then((response) => {
      console.log(response);
      setBooks(response);
    })
    .catch(err => console.log(err))
  },[]);

  //header
    const useStyles = makeStyles(theme => ({
      root: {
        flexGrow: 1,
      },
      palette: {
        primary: teal[400],
        secondary: grey[800]
      }
    }));
    const classes = useStyles();

  return(
    <div className="App">
      <header>
        <div className={classes.root}>
            <AppBar position="static" background={classes.palette} >
                <Toolbar variant="dense">
                <Typography variant="h6" className={classes.palette.secondary}> 
                    Best Books 
                </Typography>
                </Toolbar>
            </AppBar>
        </div>
      </header>
      <section>
        <Grid style={{padding:'20px'}}>
          {books.map((book, index) =>
            <Grid key={index} item xs={12} lg={4}>
              <BookCard title={book.title} author={book.author} img={book.img} />
            </Grid>
          )}
        </Grid>
      </section>
    </div>
  );
}

export default App;
